import { useState } from "react";

const faqs = [
  {
    title: "Where are these chairs assebeled?",
    text: "Do qui in laboris elit dolore nisi culpa aute qui.",
  },
  {
    title: "How long do I have to return my chair?",
    text: "Id pariatur nulla velit voluptate incididunt quis adipisicing adipisicing aliquip veniam.",
  },
  {
    title: "Do you ship countries outside India?",
    text: "Cupidatat reprehenderit consequat commodo irure do non cupidatat cupidatat amet reprehenderit anim.",
  },
];

export default function App() {
  return (
    <div className="App">
      <Accordion data={faqs} />
    </div>
  );
}

function Accordion({ data }) {
  return (
    <div className="accordion">
      {data.map((el, i) => (
        <AccordionItem title={el.title} text={el.text} num={i} />
      ))}
    </div>
  );
}

function AccordionItem({ num, title, text }) {
  const [isOpen, setIsOpen] = useState(false);

  function handleToggle() {
    setIsOpen((isOpen) => !isOpen);
  }

  return (
    <div className="item" onClick={handleToggle}>
      <p className="number">{num < 9 ? `0${num + 1}` : num + 1}</p>
      <p className="title">{title}</p>
      <p className="icon">{isOpen ? "-" : "+"}</p>
      {isOpen && <div className="content-box">{text}</div>}
    </div>
  );
}
