import { useState } from "react";

const initialItems = [
  { id: 1, desc: "Travel Bag", qnty: 1, packed: true },
  { id: 2, desc: "Mobile", qnty: 2, packed: false },
  { id: 3, desc: "Charger & Cable", qnty: 1, packed: false },
  { id: 4, desc: "Powerbank", qnty: 1, packed: false },
];

export default function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1>üå¥Let's Escaapeüå¥</h1>;
}

function Form() {
  const [desc, setDesc] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    console.log(e);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòä trip ?</h3>
      <select>
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input type="text" placeholder="Item....."></input>
      <button>Add</button>
    </form>
  );
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} key={item.id} />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  return (
    <div>
      <li>
        <span style={item.packed ? { textDecoration: "Line-through" } : {}}>
          {item.qnty} {item.desc}
        </span>
        <button>‚ùå</button>
      </li>
    </div>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>üíº You have X items on your lists and already have packed Y (Y%)</em>
    </footer>
  );
}
